import { useState } from "react";

function Navbar() {

    const [bgColor, setBgColor] = useState("");
    const [tbgColor, setTBgColor] = useState("");

    const changeColor = () => {

      if(bgColor === "#282c34"){
        setBgColor("#fff")
      }
      else{
        setBgColor("#282c34")
      }

    }
    const changeTColor = () => {

        if(tbgColor === "#fff"){
          setTBgColor("#282c34")
        }
        else{
          setTBgColor("#fff")
        }
  
      };

    const handeler = () => {
        changeColor();
        changeTColor();
    };

    return (
        <div className="navbar" style={{backgroundColor: bgColor}}>
            <div className="brand-logo" style={{color: changeTColor}}>
                Hello
            </div>
            <div className="list">
                <ul>
                    <li className="list-item" style={{fon: changeTColor}}><a href="#Home">Home</a></li>
                    <li className="list-item" style={{color: changeTColor}}><a href="#Blog">Blog</a></li>
                    <li className="list-item" style={{color: changeTColor}}><a href="#About">About</a></li>
                    <li className="list-item" style={{color: changeTColor}}><a href="#Contect">Contect Us</a></li>
                </ul>
            </div>
            <div className="theme">
                <button className="themebtn" onClick={handeler}>Theme</button>
            </div>
        </div>
    );
}

export default Navbar;
