function Navbar() {

    const [bgColor, setBgColor] = useState("");
    const [tbgColor, setTBgColor] = useState("");

    const changeColor = () => {

      if(bgColor === "#282c34"){
        setBgColor("#fff")
      }
      else{
        setBgColor("#282c34")
      }

    }
    const changeTColor = () => {

        if(tbgColor === "#282c34"){
          setBgColor("#fff")
        }
        else{
          setBgColor("#282c34")
        }
  
      }

    return (
        <div className="navbar" style={{backgroundColor: bgColor}}>
            <div className="brand-logo" style={{color: ch}}>
                Hello
            </div>
            <div className="list">
                <ul>
                    <li className="list-item"><a href="#Home">Home</a></li>
                    <li className="list-item"><a href="#Blog">Blog</a></li>
                    <li className="list-item"><a href="#About">About</a></li>
                    <li className="list-item"><a href="#Contect">Contect Us</a></li>
                </ul>
            </div>
            <div className="theme">
                <button className="themebtn" onClick={changeColor}>Theme</button>
            </div>
        </div>
    );
}

export default Navbar;
